import JqxScheduler from "jqwidgets-scripts/jqwidgets-vue/vue_jqxscheduler.vue";
import moment from "moment";
export default {
  components: {
    JqxScheduler
  },
  data: function() {
    return {
      date: new jqx.date(moment().year(), moment().month, moment().date()),
      source: new jqx.dataAdapter(this.source),
      resources: {
        colorScheme: "scheme05",
        dataField: "calendar",
        source: new jqx.dataAdapter(this.source)
      },
      appointmentDataFields: {
        from: "start",
        to: "end",
        id: "id",
        description: "description",
        location: "place",
        subject: "subject",
        resourceId: "calendar"
      },
      views: ["dayView", "weekView", "monthView"]
    };
  },
  beforeCreate: function() {
    var appointments = new Array();
    var appointment1 = {
      id: "id1",
      description: "George brings projector for presentations.",
      subject: "Quarterly Project Review Meeting",
      calendar: "Room 1",
      start: new Date(2018, 10, 20, 9, 0, 0),
      end: new Date(2018, 10, 20, 16, 0, 0)
    };
    var appointment2 = {
      id: "id2",
      subject: "IT Group Mtg.",
      calendar: "Room 2",
      start: new Date(2018, 10, 21, 10, 0, 0),
      end: new Date(2018, 10, 21, 15, 0, 0)
    };
    var appointment3 = {
      id: "id3",
      subject: "Course Social Media",
      calendar: "Room 3",
      start: new Date(2018, 10, 22, 11, 0, 0),
      end: new Date(2018, 10, 22, 13, 0, 0)
    };
    appointments.push(appointment1);
    appointments.push(appointment2);
    appointments.push(appointment3);
    this.source = {
      dataType: "array",
      dataFields: [
        { name: "id", type: "string" },
        { name: "description", type: "string" },
        { name: "location", type: "string" },
        { name: "subject", type: "string" },
        { name: "calendar", type: "string" },
        { name: "start", type: "date" },
        { name: "end", type: "date" }
      ],
      id: "id",
      localData: appointments
    };
  },
  mounted: function() {
    this.$refs.myScheduler.ensureAppointmentVisible("id1");
    let appointment1 = {
      id: "id1",
      description: "",
      subject: "New appointment",
      calendar: "Room 1",
      start: new Date(2018, 10, 23, 9, 0, 0),
      end: new Date(2018, 10, 23, 16, 0, 0)
    };
    this.$refs.myScheduler.addAppointment(appointment1);
  }
};
